package JavaBasics;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		
		//primitive data type :int/char/boolean/double
			//integer from negative to positive
			int i=20;
			int k=10;
			System.out.println(k);
			
			//double
			double a= 12.33;
			double pi=31.4;
			System.out.println(a);
			
			//characters only take single digit value and written within single quotes.
			char c='a';
			char c1='1';
			System.out.println(c1);
			char c2='&';
			
			//boolean i.e either True or False /true or false is even a keyword and a value.
			boolean b1=true;
			boolean b2=false;
			
			//string is a class not a datatype;
			String s="abcdef";
			String s1="Hello to java world";
			System.out.println(s1);
	}

}
