package JavaBasics;

public class SwapTwoNumWithThirdvariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=20;
int b=30;
int temp;
System.out.println("Before swapping number: a="+a +" b=" +b);

temp=a;// value of a stored in temp =20;
a=b;//value of b stored in a ie a=30;
b=temp; //value of temp is stored in a .i.e b=20;
System.out.println("After swapping number: a="+a +" b=" +b);

		
		
	}

}
